const texto = items[0].json.output || items[0].json?.output;

// Express√µes regulares para capturar os campos
const workflowId = texto.match(/\*\*Workflow\*\*: .*?\(ID: ([^)]+)\)/)?.[1] || null;
const execucaoId = texto.match(/\*\*Execu√ß√£o\*\*: ID (\d+)/)?.[1] || null;
const modo = texto.match(/\*\*Modo de execu√ß√£o\*\*: ([^\n]+)/)?.[1]?.trim() || null;
const dataHora = texto.match(/\*\*Data\/Hora\*\*: ([^\n]+)/)?.[1]?.trim() || null;

// Captura o link e troca 'webhook.' por 'n8n.'
const linkMatch = texto.match(/\[Clique aqui\]\((https:\/\/[^\)]+)\)/);
const link = linkMatch?.[1]?.replace('webhook.', 'n8n.') || null;

// Mensagem de erro e stack trace
const mensagemErro = texto.match(/\*\*Mensagem de erro\*\*:\n(.+?)\n\n/)?.[1]?.trim() || null;
const stackTrace = texto.match(/\*\*Stack trace\*\*:\n([\s\S]+?)\n\n‚úÖ/)?.[1]?.trim() || null;

// A√ß√£o recomendada
const recomendacao = texto.match(/‚úÖ A√ß√£o recomendada: (.+)$/)?.[1]?.trim() || null;

// Retorna o objeto com todos os dados extra√≠dos
return [
  {
    json: {
      workflow_id: workflowId,
      execucao_id: execucaoId,
      modo_execucao: modo,
      data_hora: dataHora,
      link_execucao: link,
      mensagem_erro: mensagemErro,
      stack_trace: stackTrace,
      recomendacao: recomendacao,
    }
  }
];

const crypto = require('crypto');

const appId = '18398850421';
const secret = 'Q24H7XRYJKETZ7FTTSCL3GL7XLKFV5VV';

// Pega link do produto do node "Pega produto na planilha"
const url = $('Pega produto na planilha').first().json['Product Link'];
if (!url) throw new Error('Nenhum link encontrado no campo "Product Link".');

// Extrai shopId/itemId de /product/{shopId}/{itemId} ou -i.{shopId}.{itemId}
let shopId, itemId;
let m = url.match(/\/product\/(\d+)\/(\d+)/);
if (m) { 
  shopId = m[1]; 
  itemId = m[2]; 
} else {
  m = url.match(/-i\.(\d+)\.(\d+)/);
  if (m) { 
    shopId = m[1]; 
    itemId = m[2]; 
  }
}
if (!itemId) throw new Error('N√£o foi poss√≠vel extrair itemId/shopId do link.');

// Monta query GraphQL com literais
const rawQuery = `
  query {
    productOfferV2(itemId: ${itemId}, shopId: ${shopId || 'null'}, page: 1, limit: 1) {
      nodes {
        itemId shopId productName productLink offerLink imageUrl shopName
        priceMin priceMax commissionRate sellerCommissionRate shopeeCommissionRate
        ratingStar sales
      }
      pageInfo { page limit hasNextPage }
    }
  }
`;

// Minifica
let q = rawQuery
  .replace(/\/\/.*$/gm, '')
  .replace(/\/\*[\s\S]*?\*\//g, '')
  .replace(/[\n\r]/g, ' ')
  .replace(/\s+/g, ' ')
  .replace(/\s*([(){}:,])\s*/g, '$1')
  .trim();

const graphqlPayloadObject = { query: q };
const payloadString = JSON.stringify(graphqlPayloadObject);

const timestamp = Math.floor(Date.now() / 1000);
const signatureFactor = `${appId}${timestamp}${payloadString}${secret}`;
const signature = crypto.createHash('sha256').update(signatureFactor).digest('hex');
const authorizationHeader = `SHA256 Credential=${appId}, Timestamp=${timestamp}, Signature=${signature}`;

return [{
  json: {
    url, itemId, shopId,
    authorizationHeader,
    requestBody: graphqlPayloadObject
  }
}];

// Pega o JSON retornado pelo node anterior
const input = $json;

// Acesso seguro aos dados do primeiro produto
const product = input?.data?.productOfferV2?.nodes?.[0];
if (!product) {
  throw new Error('Nenhum produto encontrado na resposta.');
}

// Monta um objeto mais limpo com os campos desejados
return [{
  json: {
    itemId: product.itemId,
    shopId: product.shopId,
    productName: product.productName,
    productLink: product.productLink,
    offerLink: product.offerLink,
    imageUrl: product.imageUrl,
    shopName: product.shopName,
    priceMin: parseFloat(product.priceMin),
    priceMax: parseFloat(product.priceMax),
    commissionRate: parseFloat(product.commissionRate),
    sellerCommissionRate: parseFloat(product.sellerCommissionRate),
    shopeeCommissionRate: parseFloat(product.shopeeCommissionRate),
    ratingStar: parseFloat(product.ratingStar),
    sales: product.sales
  }
}];

