// Extraindo os dados do node "Dados SEO"
const outlines = $node["Dados SEO"].json['outlines'];
const title = $node["Dados SEO"].json['title'];
outlines.unshift(title);

// Verificando as condições do node "Dados"
const faq = $node["Dados"].json['faq'] === true;
const conclusion = $node["Dados"].json['conclusion'] === true;
const summarization = $node["Dados"].json['summarization'] === true;

// Função para processar os outlines com hierarquia H2|H3,H3
const parseOutlines = (outlineString) => {
    const [h2, h3s] = outlineString.split('|');
    const h3List = h3s ? h3s.split(',') : [];
    return {
        h2: h2.trim(),
        h3: h3List.map(h3 => h3.trim())
    };
};

// Criando a estrutura hierárquica para outlines
const parsedOutlines = outlines.map(outline => {
    if (outline.includes('|')) {
        return parseOutlines(outline);
    } else {
        return { h2: outline, h3: [] };
    }
});

// Criando o resultado esperado pelo n8n
const result = parsedOutlines.map(parsed => ({
    json: { outlines: parsed }
}));

// Adicionando o item de conclusão se a condição for verdadeira
if (conclusion) {
    result.push({ json: { conclusion: 'Conclusion' } });
}

// Adicionando o item de FAQ se a condição for verdadeira
if (faq) {
    result.push({ json: { faq: 'FAQ' } });
}

// Adicionando o item de summarization se a condição for verdadeira
if (summarization) {
    result.push({ json: { summarization: 'Summarization' } });
}

return result;

