(() => {
  const loopKey = $node['Loop'].data.keys()[0];
  const currentRunIndex = $('Loop').context["currentRunIndex"];
  const outline = $node['Loop'].json['outlines'];

  const extractContent = () => {
    const content = $node['Extractor'].json.output.content;
    return content ? content : 'Conteúdo não disponível';
  };

  const addImage = () => {
    const imageOption = $node['Dados'].json["imagens_no_subtitulos"];
    const noImageOption = $node['Dados'].json.banco_de_imagem === "Sem imagem";

    // Verifica se a opção de imagem é "Sem imagem"
    if (noImageOption) {
      return ''; // Não adiciona imagem se a opção for "Sem imagem"
    }

    // Verifica se o node 'Wordpress - Salvar imagem em mídia' foi chamado e tem dados
    const imageSourceNode = $node['Wordpress - Salvar imagem em mídia'];
    if (!imageSourceNode || !imageSourceNode.json['source_url']) {
      return ''; // Retorna vazio se o node não foi chamado ou não tem imagem
    }

    const imageSource = imageSourceNode.json['source_url'];

    // Adiciona lógica de acordo com a opção de imagens
    if (imageOption === "NÃO") {
      return ''; // Não adiciona imagem
    } else if (imageOption === "SIM em Todos") {
      return `<img src='${imageSource}' alt='${outline.h2}' title='${outline.h2}' />`; // Adiciona imagem para todos
    } else if (imageOption === "SIM, mas intercalando" && (currentRunIndex % 2 === 0)) {
      return `<img src='${imageSource}' alt='${outline.h2}' title='${outline.h2}' />`; // Adiciona imagem de forma intercalada
    } else if (imageOption.startsWith("A cada")) {
      // Pega o número X na opção "a cada X subtítulos"
      const step = parseInt(imageOption.split(' ')[2], 10);
      if (step && currentRunIndex % step === 0) {
        return `<img src='${imageSource}' alt='${outline.h2}' title='${outline.h2}' />`; // Adiciona imagem a cada X subtítulos
      }
    }

    return ''; // Caso nenhuma das condições seja satisfeita, não adiciona imagem
  };

  if (currentRunIndex === 0) return '';

  // Outlines (agora com apenas o h2)
  if (loopKey === "outlines") {
    return `<h2>${outline.h2}</h2>${addImage()}${extractContent()}`;
  }

  // Conclusão
  if (loopKey === "conclusion") {
    return `${extractContent()}`;
  }

  return '';
})()
}}
