// Defina os campos obrigatórios conforme suas especificações
const requiredFields = ["Palavra Chave Foco", "Categoria", "Status"];

// Array para armazenar erros
let errors = [];

// Verificar se o node "Google Sheets - Ler Palavras Chaves" existe e tem dados
const sheetData = $node["Google Sheets - Ler Palavras Chaves"]?.json;
if (!sheetData) {
  errors.push('O node "Google Sheets - Ler Palavras Chaves" não foi encontrado ou não contém dados.');
}

// Verificar se o node "variaveis" existe e tem o campo "URL do Site"
let urlSite = $node["variaveis"]?.json?.["URL do Site"] || '';
if (!urlSite) {
  errors.push('O campo "URL do Site" é obrigatório e não foi fornecido pelo node "variaveis".');
}

// Adicionar log para depuração
console.log("Valor fornecido para URL do Site:", urlSite);

// Remover espaços em branco nas extremidades e substituir "http" por "https"
urlSite = urlSite.trim().replace(/^http:\/\//, 'https://');

// Remover barra final se existir
if (urlSite.endsWith('/')) {
  urlSite = urlSite.slice(0, -1);
}

// Verificar se o URL é válido
if (!urlSite.match(/^https:\/\/[^\s/$.?#].[^\s]*$/)) {
  errors.push(`O campo "URL do Site" está malformado. Valor fornecido: "${urlSite}"`);
}

// **Função para Extrair IDs das Categorias e Tags**
function extractIds(field) {
  return field
    .split(',')
    .map(item => item.trim().split(' - ')[0])
    .join(',');
}

// **Validação das Tags**
const tags = sheetData["Tags"] || "";

if (tags.trim() !== "") {
  const tagsLower = tags.toLowerCase().trim();

  if (tagsLower !== "automatico") {
    const tagList = tags.split(',').map(tag => tag.trim());
    const invalidTags = tagList.filter(tag => !/^\d+\s*-\s*.+/.test(tag));

    if (invalidTags.length > 0) {
      errors.push(`O campo "Tags" possui valores inválidos. Cada tag deve estar no formato "ID - Nome da Tag" ou conter a palavra "automatico". Tags inválidas: ${invalidTags.join(', ')}`);
    }
  }
}

// **Validação para verificar se "Categoria" contém um número**
if (!sheetData["Categoria"] || !sheetData["Categoria"].match(/\d+/)) {
  errors.push('O campo "Categoria" deve conter o ID da categoria.');
}

let categoryIds = extractIds(sheetData["Categoria"] || "");
let tagIds = extractIds(sheetData["Tags"] || "");

console.log(`ID das Categorias Normalizado: ${categoryIds}`);
console.log(`ID das Tags Normalizado: ${tagIds}`);

// **Tratar Status**
const statusMap = {
  "Rascunho": "draft",
  "Publicado": "publish",
  "Agendado": "future",
  "Revisão pendente": "pending",
  "Privado": "private"
};

let status = sheetData ? sheetData["Status"] || "" : "";
let normalizedStatus = statusMap[status] || 'future';

// Adicionar log para depuração do status
console.log(`Status original: "${status}", Status normalizado: "${normalizedStatus}"`);

// **Validar e Detectar Valores Invertidos de Data e Hora**
let data = sheetData ? sheetData["Data"] || "" : "";
let hora = sheetData ? sheetData["Hora"] || "" : "";

// Verificar e corrigir se data e hora estão invertidas
if (data.match(/^\d{2}:\d{2}$/) && hora.match(/^\d{2}\/\d{2}\/\d{4}$/)) {
  console.warn("Detectamos que os campos Data e Hora estão invertidos. Corrigindo...");
  [data, hora] = [hora, data];
}

// Verificar preenchimento condicional de data e hora
if ((data && !hora) || (!data && hora)) {
  errors.push(`Se um dos campos "Data" ou "Hora" for preenchido, ambos devem ser fornecidos.`);
}

// Converter para ISO apenas se ambos estiverem presentes e válidos
let isoDateTime = "";
if (data && hora) {
  if (!data.match(/^\d{2}\/\d{2}\/\d{4}$/)) {
    errors.push(`A data fornecida é inválida: "${data}". Use o formato DD/MM/AAAA.`);
  }
  if (!hora.match(/^\d{2}:\d{2}$/)) {
    errors.push(`A hora fornecida é inválida: "${hora}". Use o formato hh:mm.`);
  }

  try {
    let [day, month, year] = data.split('/');
    let [hour, minute] = hora.split(':');

    // Converter para números inteiros
    hour = parseInt(hour, 10);
    minute = parseInt(minute, 10);

    // Ajuste de fuso horário (UTC+3)
    hour = (hour + 3) % 24;

    isoDateTime = `${year}-${month.padStart(2, '0')}-${day.padStart(2, '0')}T` +
                  `${hour.toString().padStart(2, '0')}:${minute.toString().padStart(2, '0')}:00.000Z`;

    console.log(`Data e hora convertidas para ISO: ${isoDateTime}`);
  } catch (error) {
    console.error(`Erro ao converter data e hora: ${error.message}`);
    errors.push(`Erro ao converter data e hora.`);
  }
}

// **Validação do Banco de Imagem e Campo "Imagem"**
const imageBanksRequiringPrompt = [
  "Piwigo", "Pexels", "Pixabay", "Unsplash", 
  "URL", "GPT image", "Leonardo.AI", 
  "Midjourney", "Pollinations", "Flux", "ImageKit"
];

if (imageBanksRequiringPrompt.includes(sheetData["Banco de imagem"]) && 
    (!sheetData["Imagem"] || sheetData["Imagem"].trim() === "")) {
  errors.push(`O campo da coluna "Imagem" é obrigatório quando o Banco de imagem for "${sheetData["Banco de imagem"]}".`);
}

// **Tratar Campos Booleanos com Default False**
const booleanFields = ["Vídeo", "Resumo", "Conclusão", "FAQ", "Link Interno", "CTA"];
booleanFields.forEach(field => {
  sheetData[field] = sheetData[field] === true ? true : false;
});

// **Validação do campo "Vídeo ID"**
if (sheetData["Vídeo"] === true && (!sheetData["Vídeo ID"] || sheetData["Vídeo ID"].trim() === "")) {
  errors.push('O campo "Vídeo ID" é obrigatório quando o campo "Vídeo" está marcado como verdadeiro.');
}

// **Validação do campo "CTA"**
if (sheetData["CTA"] === true) {
  if (!sheetData["CTA Texto"] || sheetData["CTA Texto"].trim() === "") {
    errors.push('O campo "CTA Texto" é obrigatório quando o campo "CTA" está marcado como verdadeiro.');
  }
  if (!sheetData["CTA Link"] || sheetData["CTA Link"].trim() === "") {
    errors.push('O campo "CTA Link" é obrigatório quando o campo "CTA" está marcado como verdadeiro.');
  }
}

// Adicionar os dados ao item final
const finalData = {
  ...sheetData,
  "URL do Site": urlSite,
  "Categoria": categoryIds,
  "Tags": tagIds,
  "Status": normalizedStatus,
  "DataHoraISO": isoDateTime
};

// Validar que pelo menos "Palavra Chave Foco" ou "Título" esteja preenchido
const keyword = sheetData["Palavra Chave Foco"]?.trim() || "";
const titulo = sheetData["Título"]?.trim() || "";

if (!keyword && !titulo) {
  errors.push('Pelo menos um dos campos "Palavra Chave Foco" ou "Título" deve ser preenchido.');
}

// Validar campos obrigatórios, exceto "Palavra Chave Foco" (tratado acima)
requiredFields.forEach(field => {
  if (field !== "Palavra Chave Foco" && (!finalData[field] || finalData[field].toString().trim() === "")) {
    errors.push(`O campo "${field}" é obrigatório e está vazio.`);
  }
});

// Validação de "Palavra Chave Foco" para não ser só espaços em branco, se estiver preenchido
if (keyword && keyword === "") {
  errors.push(`O campo "Palavra Chave Foco" não pode ser apenas espaços em branco.`);
}

// Validação de "Palavra Chave Foco" para não ser apenas espaços em branco
if (finalData["Palavra Chave Foco"] && finalData["Palavra Chave Foco"].trim() === "") {
  errors.push(`O campo "Palavra Chave Foco" não pode ser apenas espaços em branco.`);
}

// Se houver erros, registrar e lançar exceção com tratamento adequado
if (errors.length > 0) {
  const errorMessage = errors.map(e => e.trim()).join(" | ");
  console.error("Erros encontrados:", errorMessage);
  throw new Error("Validação falhou: " + errorMessage);
}
// Se não houver erros, retornar os dados finais
return finalData;

